import React, { useState, useRef } from "react";
import { useForm } from "react-hook-form";
import axios from "axios";

function SignUpPage() {
  const { register, watch, errors, handleSubmit } = useForm(); // useForm({ mode: "onChange" });
  // console.log(watch("email"));
  const userpwd = useRef();
  userpwd.current = watch("userpwd");

  const [loading, setLoading] = useState(false);

  const onSumit = async (data) => {
    setLoading(true);
    console.log(data);
    const { email, name, userpwd, phone } = data;
    try {
      const response = await axios.post("/api/user/user", {
        email,
        name,
        userpwd,
        phone,
      });
      console.log(response);
    } catch (error) {
      alert(error.response.data);
    }
    setLoading(false);
  };
  return (
    <div style={{ width: "1024px", margin: "50px auto" }}>
      <form onSubmit={handleSubmit(onSumit)}>
        <label>Name</label>
        <input
          name="name"
          type="text"
          placeholder="EX) 홍길동"
          {...register("name",{
            required: true,
            pattern: /^[가-힣]{2,7}$/,
          })}
        />
        <label>Email</label>
        <input
          name="email"
          type="email"
          placeholder="EX ) mindcrying@naver.com"
          {...register("email",{
            required: true,
            pattern:
              /^[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*.[a-zA-Z]{2,3}$/i,
          })}
        />
        <label>Phone</label>
        <input
          name="phone"
          type="number"
          placeholder="연락처는 숫자로만 입력해주세요. ex) 01012345678"
          {...register("phone", {
            required: true,
            pattern: /(^02.{0}|^01.{1}|[0-9]{3})([0-9]+)([0-9]{4})/g,
          })}
        />
        <label>Password</label>
        <input
          name="userpwd"
          type="password"
          {...register("userpwd", {
            required: true,
            pattern:
              /(?=.*\d{1,50})(?=.*[~`!@#$%\^&*()-+=]{1,50})(?=.*[a-zA-Z]{2,50}).{8,16}$/,
          })}
        />

        <label>Password Confirm</label>
        <input
          name="userpwd_confirm"
          type="password"
          {...register("userpwd_confirm",{
            required: true,
            validate: (value) => value === userpwd.current,
          })}
        />
        <input type="submit" disabled={loading} />
      </form>
    </div>
  );
}

export default SignUpPage;
