//회원가입 페이지
//API 주소 추가 필요

import React, { useRef } from "react";
import { useForm } from "react-hook-form";

import "./SignUpPage.css";

function SignUpPage() {
    const { signup, watch, errors, handleSubmit} = useForm();
    const userpwd = useRef();
    userpwd.current = watch("userpwd");
    const [loading, setLoading] = useState(false);

    const onSumit = async(data) => {
        setLoading(true);
        console.log(data);
        const {userEmail, userpwd, userName} = data;
        try{
            const response = await axios.post("API 주소", {
                userEmail,
                userpwd,
                userName,
            });
            console.log(response);
        }
        catch(error) {
            alert(error.response.data);
        }
        setLoading(false);
    };
    

    return(
        <div className="Signup_Container">
            <div className="SignUp">
                <p style={{fontSize:"30px"}}> create an <b>Account</b></p>
                <p style={{backgroundColor : "#f3e77e79", fontSize:"13px", marginBottom:"50px"}}> 개인화된 맞춤형 추천 서비스를 받아보세요! </p>
                
                <form onSubmit={handleSubmit(onSumit)}>
                    <div className="Signup_textArea">
                        <label htmlFor="userEmail" className="Text_Name"> e-mail </label>
                        <input
                            type="email"
                            id="userEmail"
                            name="userEmail"
                            placeholder="e-mail"
                            className="Text_input"
                        />
                    </div>
                    <div className="Signup_textArea">
                        <label htmlFor="userPassword" className="Text_Name"> password </label>
                        <input
                            type="password"
                            id="userPassword"
                            name="userPassword"
                            placeholder="password"
                            className="Text_input"
                        />
                    </div>
                    <div className="Signup_textArea">
                        <label htmlFor="checkUserPassword" className="Text_Name"> check password </label>
                        <input
                            type="password"
                            id="checkUserPassword"
                            name="checkUserPassword"
                            className="Text_input"
                        />
                    </div>
                    <div className="Signup_textArea">
                        <label htmlFor="userName" className="Text_Name"> nickname </label>
                        <input
                            type="text"
                            id="userName"
                            name="userName"
                            placeholder="Matt"
                            className="Text_input"
                            ref={signup({
                                required:true,
                                pattern : /^[a-zA-Z]{2,7}$/,
                            })}
                        />
                        {errors.userName && errors.userName.type === "required" && (
                            <p> 닉네임을 입력해주세요 </p>
                        )}
                        {errors.userName && errors.userName.type === "pattern" && (
                            <p> 이름을 2-7자로 작성해주세요 </p>
                        )}
                    </div>
                    <button className="SignupBtn"> SIGN UP </button>
                </form>
            </div>
        </div>
    )
}

export default SignUpPage;
