//회원가입 페이지
//API 주소 추가 필요

import React, { useRef } from "react";

import "./SignUpPage.css";

function SignUpPage() {
    // form 가져오기
    const signupFormRef = useRef(null);

    // form 제출하기
    const submitSignUp = (e) => {
        e.preventDefault();

        const signupForm = signupFormRef.current;
        const formData = new FormData(signupForm);
        
        //API 주소 추가필요
        fetch("API 주소", {
            method : "POST",
            headers : {
                "Content-Type" : "application/json",
            },
            body : formData,
        })
        .then((response) => {
            if (response.ok === true){
                return response.json();
            }
        })
        .finally(()=>{
            for(const key of formData.values()){
                console.log(key);
            }

        })
    }

    return(
        <div className="Signup_Container">
            <div className="SignUp">
                <p style={{fontSize:"30px"}}> create an <b>Account</b></p>
                <p style={{backgroundColor : "#f3e77e79", fontSize:"13px", marginBottom:"50px"}}> 개인화된 맞춤형 추천 서비스를 받아보세요! </p>
                
                <form id="signupForm" onSubmit={submitSignUp}>
                    <div className="Signup_textArea">
                        <label htmlFor="userEmail" className="Text_Name"> e-mail </label>
                        <input
                            type="email"
                            id="userEmail"
                            name="userEmail"
                            placeholder="e-mail"
                            className="Text_input"
                        />
                    </div>
                    <div className="Signup_textArea">
                        <label htmlFor="userPassword" className="Text_Name"> password </label>
                        <input
                            type="password"
                            id="userPassword"
                            name="userPassword"
                            placeholder="password"
                            className="Text_input"
                        />
                    </div>
                    <div className="Signup_textArea">
                        <label htmlFor="checkUserPassword" className="Text_Name"> check password </label>
                        <input
                            type="password"
                            id="checkUserPassword"
                            name="checkUserPassword"
                            className="Text_input"
                        />
                    </div>
                    <div className="Signup_textArea">
                        <label htmlFor="userName" className="Text_Name"> nickname </label>
                        <input
                            type="text"
                            id="userName"
                            name="userName"
                            placeholder="Matt"
                            className="Text_input"
                        />
                    </div>
                    <button className="SignupBtn"> SIGN UP </button>
                </form>
            </div>
        </div>
    )
}

export default SignUpPage;
